export default {
  ...
  firebase: {
    config: {
      apiKey: "",
      authDomain: "",
      databaseURL: "",
      projectId: "",
      storageBucket: "",
      messagingSenderId: "",
      appId: ""
    },
    services: {
      database: true,
      auth: {
        initialize: {
          onAuthStateChangedMutation: 'auth/SET_USER'
        },
      }
    }
  }
  ...
}

export default ({ store, redirect }) => {
  if (!store.state.auth.user) {
    redirect('/')
  }
}

export const state = () => ({
  user: null
})

export const mutations = {
  SET_USER(state, { authUser }) {
    if (authUser) {
      const { displayName, photoURL } = authUser
      state.user = {
        displayName,
        photoURL
      }
    } else {
      state.user = null
    }
  }
}
