import Vue from 'vue'
import Router from 'vue-router'

import UserPage from '~/pages/users/user.vue'

Vue.use(Router)

export function createRouter() {
  return new Router({
    mode: 'history',
    routes: [
      {
        path: '/users/{user}',
        component: UserPage
      }
    ]
  })
}
